$("body").append($("<input>").attr("placeholder","Add new task").addClass("input")).append($("<button>").text("Add Task").addClass("btn").click(()=>{$(".tasklist").append($("<li>").append($("<span>").text($("input").val())).append($("<button>").text("↑").addClass("up").click(t=>{$(t.target).parent().prev().before($(t.target).parent())})).append($("<button>").text("↓").addClass("down").click(t=>{$(t.target).parent().before($(t.target).parent().next())})).append($("<button>").text("❌").click(t=>{$(t.target).parent().remove()}))),$("input").val("")})).append($("<ul>").addClass("tasklist"));